function formateDate(date){
    if(!date){
        date = new Date()
    }
    var year = date.getFullYear()
    var month = date.getMonth() + 1
    var day = date.getDate()
    if(month < 10) {
        month = '0' + month
    }
    if (day < 10) {
        day = '0' + date
    }
    return year + '-' + month + '-' day
}
var date = new Date()
var Date = formateDate(date)
console.log(Date)
